mapper.select:
SELECT mapping_id, account_id, mapping_name, is_active, change_type, note, created_at, updated_at
FROM mappings
WHERE mapping_id = #{id}

mapper.update:
UPDATE mappings
SET mapping_name = #{name}, is_active = #{isActive}, change_type = #{changeType}, note = #{note}, updated_at = #{updatedAt}
WHERE mapping_id = #{id}

mapper.select:
SELECT EXISTS(
    SELECT 1 FROM mappings
    WHERE account_id = #{accountId} AND mapping_name = #{name}
)

mapper.update:
UPDATE mappings
SET mapping_name = #{name}, updated_at = NOW()
WHERE mapping_id = #{id}
